import React, { useState } from 'react';
import './style.css';

const boardSize = 15; // 定义棋盘大小为15x15
const initialSquares = Array(boardSize * boardSize).fill(null);

const calculateWinner = (squares) => {
  // 检查水平和垂直方向
  for (let i = 0; i < boardSize; i++) {
    for (let j = 0; j < boardSize - 4; j++) {
      const horizontalLine = [i * boardSize + j, i * boardSize + j + 1, i * boardSize + j + 2, i * boardSize + j + 3, i * boardSize + j + 4];
      const verticalLine = [j * boardSize + i, (j + 1) * boardSize + i, (j + 2) * boardSize + i, (j + 3) * boardSize + i, (j + 4) * boardSize + i];
      if (checkLine(squares, horizontalLine)) return squares[horizontalLine[0]];
      if (checkLine(squares, verticalLine)) return squares[verticalLine[0]];
    }
  }

  // 检查两个对角线方向
  for (let i = 0; i < boardSize - 4; i++) {
    for (let j = 0; j < boardSize - 4; j++) {
      const diagonalLine1 = [i * boardSize + j, (i + 1) * boardSize + j + 1, (i + 2) * boardSize + j + 2, (i + 3) * boardSize + j + 3, (i + 4) * boardSize + j + 4];
      const diagonalLine2 = [i * boardSize + (boardSize - 1 - j), (i + 1) * boardSize + (boardSize - 2 - j), (i + 2) * boardSize + (boardSize - 3 - j), (i + 3) * boardSize + (boardSize - 4 - j), (i + 4) * boardSize + (boardSize - 5 - j)];
      if (checkLine(squares, diagonalLine1)) return squares[diagonalLine1[0]];
      if (checkLine(squares, diagonalLine2)) return squares[diagonalLine2[0]];
    }
  }

  return null;
};

const checkLine = (squares, line) => {
  const first = squares[line[0]];
  return first && line.every((index) => squares[index] === first);
};

const App = () => {
  const [squares, setSquares] = useState(initialSquares);
  const [xIsNext, setXIsNext] = useState(true);
  const winner = calculateWinner(squares);

  const handleClick = (i) => {
    if (winner || squares[i]) return;
    const nextSquares = squares.slice();
    nextSquares[i] = xIsNext ? 'X' : 'O';
    setSquares(nextSquares);
    setXIsNext(!xIsNext);
  };

  const renderSquare = (i) => (
    <button className="square" onClick={() => handleClick(i)}>
      {squares[i]}
    </button>
  );

  const restartGame = () => {
    setSquares(initialSquares);
    setXIsNext(true);
  };

  return (
    <div className="game">
      <div className="game-board">
        {Array.from({ length: boardSize }, (_, i) => (
          <div key={i} className="board-row">
            {Array.from({ length: boardSize }, (_, j) => renderSquare(i * boardSize + j))}
          </div>
        ))}
      </div>
      <div className="game-info">
        <div>{winner ? `Winner: ${winner}` : `Next player:${xIsNext ? 'X' : 'O'}`}</div>
        <button onClick={restartGame}>Restart Game</button>
      </div>
    </div>
  );
};

export default App;
